import React, { Component } from 'react';
import './style.scss';
import cardData from "../../cards.json";
import Card from '../Card';
import NavBar from '../NavBar';

class Game extends Component {

    state = {
        score: 0,
        highScore: 0,
        data: cardData,
        clickedList: []
    }

    handleClick = (cardId) => {
        const {score, clickedList } = this.state;
        const isClicked = clickedList.includes(cardId);

        if (isClicked) {
            this.setState({
                score: 0
            })        
        }
        else {

            this.setState({
                clickedList: [...clickedList, cardId],
                score: score + 1
            })
        }        
    }

    render() {
        return (
            <div>
              <NavBar 
                headingText="Score:"
                headingValue={this.state.score}
              />
               {this.state.data.map((card) => (
                    <Card   
                         key={card.id}
                        id={card.id}
                        image={card.image} 
                        name={card.name} 
                        handleClick={this.handleClick}
                    />  
                 ))}    
            </div>
        );
    }
}

export default Game;
